create table lhjbase.t_employee
(
    id int unsigned auto_increment comment '主键'
        primary key,
    login_name varchar(30) charset utf8 not null comment '登录帐号',
    login_pwd varchar(50) charset utf8 not null comment '登录密码',
    actual_name varchar(30) charset utf8 not null comment '员工名称',
    nick_name varchar(30) default '' null comment '别名',
    phone varchar(15) charset utf8 null comment '手机号码',
    id_card varchar(18) charset utf8 null comment '身份证',
    birthday date null comment '出生日期',
    email varchar(50) null comment '邮箱',
    department_id int unsigned not null comment '部门id',
    is_leave int default 0 not null comment '是否离职1是',
    is_disabled int default 0 not null comment '是否被禁用 0否1是',
    remark varchar(200) null comment '备注',
    create_user int unsigned not null comment '创建者id',
    update_time datetime default CURRENT_TIMESTAMP not null on update CURRENT_TIMESTAMP comment '更新时间',
    create_time datetime default CURRENT_TIMESTAMP not null comment '创建时间',
    is_delete int default 0 not null comment '是否删除0否 1是'
)
    comment '员工表';

